import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

const UpdateModal = ({
  id,
  team,
  teams,
  setTeams,
  error,
  setError,
  pokemons,
}) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const defaultState = {
    name: '',
    speciesToRemove: '',
    SpeciesToAdd: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const updateTeam = async (id) => {
    const team = protoTeamBuilder();
    try {
      const response = await fetch(`http://localhost:8080/api/v1/teams/${id}`, {
        method: 'PUT',
        headers: {
          'Content-type': 'application/json',
        },
        body: JSON.stringify(team),
      });
      console.log(response);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const protoTeamBuilder = () => {
    const oldTeam = team;
    const speciesToRemoveArray = inputState.speciesToRemove.split(',');

    speciesToRemoveArray.forEach((s) => {
      oldTeam = oldTeam.filter((p) => p.species != s);
    });

    const oldTeamPostRemove = oldTeam.filter((p) => p.species !== s);
    const SpeciesToAddArray = inputState.SpeciesToAdd.split(',');
  };

  return (
    <>
      <Button
        onClick={handleShow}
        className='d-flex justify-content-between m-2'
        variant='primary'
        id='delete'>
        <i className='fa fa-gear' />
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <Form.Group className='mb-3' controlId='name'>
            <Form.Control
              type='text'
              value={inputState.name}
              placeholder='Enter the new team name'
              onChange={(event) => {
                handleInputChange(event.target.id, event.target.value);
              }}
            />
          </Form.Group>
          <Form.Group className='mb-3' controlId='pokesToRemove'>
            <Form.Control
              type='text'
              value={inputState.speciesToRemove}
              placeholder='Enter pokemons to remove separated by comma'
              onChange={(event) => {
                handleInputChange(event.target.id, event.target.value);
              }}
            />
          </Form.Group>
          <Form.Group className='mb-3' controlId='pokesToAdd'>
            <Form.Control
              type='text'
              value={inputState.SpeciesToAdd}
              placeholder='Enter pokemons to add separated by comma'
              onChange={(event) => {
                handleInputChange(event.target.id, event.target.value);
              }}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={handleClose}>
            Close
          </Button>
          <Button
            variant='primary'
            onClick={() => {
              updateTeam(id);
              handleClose();
            }}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default UpdateModal;
