import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const UpdateModal = ({ team, id, teams, setTeams, error, setError }) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const updateTeam = async (team, id) => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/teams/${id}`, {
        method: 'PUT',
        headers: {
          'Content-type': 'application/json',
        },
        body: JSON.stringify(team),
      });
      console.log(response);
    } catch (e) {
      setError(e.message);
    }
  };

  return (
    <>
      <Button
        onClick={handleShow}
        className='d-flex justify-content-between m-2'
        variant='primary'
        id='delete'>
        <i className='fa fa-gear' />
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <Form.Group className='mb-3' controlId='pokesToRemove'>
            <Form.Control
              type='text'
              placeholder='Enter pokemons to remove separated by comma'
            />
          </Form.Group>
          <Form.Group className='mb-3' controlId='formBasicEmail'>
            <Form.Label>Email address</Form.Label>
            <Form.Control type='email' placeholder='Enter email' />
            <Form.Text className='text-muted'>
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={handleClose}>
            Close
          </Button>
          <Button variant='primary' onClick={handleClose}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default UpdateModal;
