import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const PokeModal = ({ dexNumber, getStringDexNumber }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getImageURL = (dexNumber) => {
    switch (species) {
      case 'NIDORAN_F':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-f.png';
      case 'NIDORAN_M':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-m.png';
      case 'MR_MIME':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/mr-mime.png';
      case 'FARFETCH_D':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/farfetchd.png';
    }
    return `http://pearlsaurus.fc2web.com/elog/386/${getStringDexNumber(
      dexNumber
    )}.jpg`;
  };

  return (
    <>
      <Button variant='primary' onClick={handleShow}>
        SPLASH ART
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton />
        <Modal.Body>
          <img
            src={`./images/${getStringDexNumber(dexNumber)}.jpg`}
            width='100%'
          />
        </Modal.Body>
      </Modal>
    </>
  );
};

export default PokeModal;
