import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TeamForm({ baseURI, pokemons, error, setError }) {
  const [inputErrors, setInputErrors] = useState({});

  const defaultState = {
    name: '',
    member01: '',
    member02: '',
    member03: '',
    member04: '',
    member05: '',
    member06: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const findBySpecies = async (species) => {
    try {
      const response = await fetch(baseURI + `${species.toUpperCase()}`);
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log(data);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
  };

  return (
    <>
      <Form onSubmit={handleSubmit}>
        <Form.Group className='mb-3' controlId='name'>
          <Form.Control
            type='text'
            value={inputState.name}
            placeholder='Enter the team name'
            onChange={(event) => {
              console.log(event.target.value);
              handleInputChange(event.target.id, event.target.value);
            }}
            isInvalid={inputErrors.name ? true : false}
          />
          <Form.Control.Feedback type='invalid'>
            {inputErrors.name}
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Select
          aria-label='Select species'
          onChange={(event) => {
            findBySpecies(event.target.value.toUpperCase());
          }}>
          <option></option>
          {pokemons.map((pokemon) => (
            <option>{pokemon.species.toLowerCase()}</option>
          ))}
        </Form.Select>

        <div className='text-center'>
          <Button
            variant='primary'
            type='submit'
            id='btnSubmit'
            disabled='true'>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
