import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const PokeModal = ({ dexNumber, getStringDexNumber }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getImageURL = (stringDexNumber) => {
    switch (stringDexNumber) {
      case '003' ||
        '006' ||
        '009' ||
        '012' ||
        '016' ||
        '024' ||
        '031' ||
        '037' ||
        '048' ||
        '056' ||
        '058' ||
        '062' ||
        '065' ||
        '075' ||
        '077' ||
        '112' ||
        '113' ||
        '115' ||
        '121' ||
        '131' ||
        '133' ||
        '137' ||
        '147' ||
        '149':
        imageURL = `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_2.jpg`;
        return imageURL;
      case '018' ||
        '026' ||
        '038' ||
        '042' ||
        '068' ||
        '071' ||
        '076' ||
        '078' ||
        '082' ||
        '085' ||
        '087' ||
        '089' ||
        '091' ||
        '103' ||
        '106' ||
        '110' ||
        '117' ||
        '123' ||
        '135' ||
        '139' ||
        '145' ||
        '146':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_700.jpg`;
      case '043' || '53':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_3.jpg`;
      case '059' || '144':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_2_700.jpg`;
      case '150':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_4.jpg`;
      case '151':
        return 'https://scontent-mxp1-1.xx.fbcdn.net/v/t1.18169-9/1002830_491880754231364_1455784644_n.jpg?_nc_cat=111&ccb=1-7&_nc_sid=cdbe9c&_nc_ohc=zelUqfMVGa8AX-Dw6uK&_nc_ht=scontent-mxp1-1.xx&oh=00_AfDiuL1sv9FTwpsHftR13qlzZKexkXyQadb47YFGrMmucg&oe=6432CED8';
      default:
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}.jpg`;
    }
  };

  return (
    <>
      <Button variant='primary' onClick={handleShow}>
        SPLASH ART
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton />
        <Modal.Body>
          <img src={getImageURL(getStringDexNumber(dexNumber))} width='100%' />
        </Modal.Body>
      </Modal>
    </>
  );
};

export default PokeModal;
