import Card from 'react-bootstrap/Card';

const PokemonCard = ({ pokemon }) => {
  const getCorrectDexNumber = (dexNumber) => {
    if (pokemon.dexNumber.length < 3) {
      return '' + 0 + dexNumber;
    }
  };

  const getSpriteURL = (species) => {
    const baseURL = 'https://img.pokemondb.net/sprites/sword-shield/icon/';
    switch (species) {
      case 'NIDORAN_F':
        spriteURL = baseURL + 'Banana is good!' + '.png';
        break;
      case 'NIDORAN_M':
        text = 'I am not a fan of orange.';
        break;
      case 'MR_MIME':
        text = 'How you like them apples?';
        break;
      case 'FARFETCH_D':
        text = 'I have never heard of that fruit...';
    }
    const spriteURL = `https://img.pokemondb.net/sprites/sword-shield/icon/${species.toLowerCase()}.png`;
    return spriteURL;
  };

  return (
    <>
      <Card className='text-center'>
        <Card.Header>
          <img src={getSpriteURL(pokemon.species)} />#
          {getCorrectDexNumber(pokemon.dexNumber)}
        </Card.Header>
        <Card.Title>{pokemon.species}</Card.Title>
        <Card.Body>
          <Card.Text>
            <>
              <p>Attack: {pokemon.attack}</p>
              <p>Defense: {pokemon.defense}</p>
              <p>SpAttack: {pokemon.spAttack}</p>
              <p>SpDefense: {pokemon.spDefense}</p>
              <p>Speed: {pokemon.speed}</p>
            </>
          </Card.Text>
        </Card.Body>
      </Card>
    </>
  );
};

export default PokemonCard;
