import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TeamForm({ baseURI, pokemons, error, setError, getSpriteURL }) {
  const [members, setMembers] = useState([]);
  const [submitDisable, setSubmitDisable] = useState(true);
  const [selectDisable, setSelectDisable] = useState(false);

  const defaultState = {
    name: '',
    member01: '',
    member02: '',
    member03: '',
    member04: '',
    member05: '',
    member06: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const findBySpecies = async (species) => {
    try {
      const response = await fetch(baseURI + `/${species.toUpperCase()}`);
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const data = await response.json();
      const newMembers = [...members, data];
      setMembers(newMembers);
      console.log(newMembers);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
  };

  return (
    <>
      <Form onSubmit={handleSubmit}>
        <Form.Group className='mb-3' controlId='name'>
          <Form.Control
            type='text'
            value={inputState.name}
            placeholder='Enter the team name'
            onChange={(event) => {
              console.log(event.target.value);
              handleInputChange(event.target.id, event.target.value);
            }}
          />
        </Form.Group>

        <Form.Select
          disabled={selectDisable}
          className='mb-3'
          aria-label='Select species'
          onChange={(event) => {
            setSubmitDisable(false);
            const speciesStr = event.target.value;
            if (speciesStr.length > 0) {
              findBySpecies(speciesStr.toUpperCase());
            }
          }}>
          <option></option>
          {pokemons.map((pokemon) => (
            <option key={pokemon.dexNumber}>
              {pokemon.species.toLowerCase()}
            </option>
          ))}
        </Form.Select>

        <div className='m-3'>
          {members.map((member) => (
            <span key={member.dexNumber} className='mx-2'>
              <img
                src={getSpriteURL(member.species)}
                className='border border-dark rounded'
                alt='pokeSprite'
              />
            </span>
          ))}
        </div>

        <div className='text-center'>
          <Button
            variant='primary'
            type='submit'
            id='btnSubmit'
            disabled={submitDisable}>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
