import Card from 'react-bootstrap/Card';

const PokemonCard = ({ pokemon }) => {
  const getSpriteURL = (species) => {
    const spriteURL = `https://img.pokemondb.net/sprites/sword-shield/icon/${species.toUpperCase()}.png`;
    return spriteURL;
  };

  return (
    <>
      <Card className='text-center'>
        <Card.Header>
          <img src='https://img.pokemondb.net/sprites/sword-shield/icon/.png' />
          {pokemon.species} #{pokemon.dexNumber}
        </Card.Header>
        <Card.Body>
          <Card.Text>
            <>
              <p>Attack: {pokemon.attack}</p>
              <p>Defense: {pokemon.defense}</p>
              <p>SpAttack: {pokemon.spAttack}</p>
              <p>SpDefense: {pokemon.spDefense}</p>
              <p>Speed: {pokemon.speed}</p>
            </>
          </Card.Text>
        </Card.Body>
      </Card>
    </>
  );
};

export default PokemonCard;
