import Accordion from 'react-bootstrap/Accordion';
import Button from 'react-bootstrap/Button';
import UpdateModal from './UpdateModal';
import { deleteTeamById } from './APIs/TeamAPI';
import { findAllTeams } from './APIs/TeamAPI';

const TeamAccordion = ({ getSpriteURL, teams, setTeams, pokemons }) => {
  return (
    <>
      <Accordion>
        {findAllTeams.map((team) => (
          <Accordion.Item eventKey={team.id}>
            <Accordion.Header>
              <UpdateModal
                team={team}
                id={team.id}
                teams={teams}
                setTeams={setTeams}
                pokemons={pokemons}
              />
              <Button
                className='d-flex justify-content-between m-2'
                variant='danger'
                id='delete'
                onClick={() => {
                  deleteTeamById(team.id);
                  const newTeams = teams.filter((t) => t.id !== team.id);
                  setTeams(newTeams);
                }}>
                <i className='fa fa-trash' />
              </Button>
              Team '{team.name}'
            </Accordion.Header>
            <Accordion.Body>
              {team.members.map((member) => (
                <img src={getSpriteURL(member.species)} alt={member.species} />
              ))}
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
    </>
  );
};

export default TeamAccordion;
