import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import CarouselPokes from './components/CarouselPokes';
import { useState, useEffect } from 'react';
import TeamForm from './components/TeamForm';
import TeamAccordion from './components/TeamAccordion';
import { findAllPokemon } from './components/APIs/PokemonAPI';
import { findAllTeams } from './components/APIs/TeamAPI';
import { deleteAllTeams } from './components/APIs/TeamAPI';

const App = () => {
  const [pokemons, setPokemons] = useState([]);
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    setPokemons(findAllPokemon);
    setTeams(findAllTeams);
  }, []);

  const getSpriteURL = (species) => {
    switch (species) {
      case 'NIDORAN_F':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-f.png';
      case 'NIDORAN_M':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-m.png';
      case 'MR_MIME':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/mr-mime.png';
      case 'FARFETCH_D':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/farfetchd.png';
      default:
        return `https://img.pokemondb.net/sprites/sword-shield/icon/${species.toLowerCase()}.png`;
    }
  };

  const handleSubmit = (value) => {};

  return (
    <>
      <Navbar expand='lg' variant='dark' bg='dark'>
        <Container id='nav-container'>
          <Navbar.Brand>POKEDEX</Navbar.Brand>
          <Form
            className='d-flex'
            onSubmit={(event) => {
              event.preventDefault();
              handleSubmit(event.target.value);
            }}>
            <Form.Control
              type='search'
              placeholder='Search'
              className='me-2'
              aria-label='Search'
            />
            <Button variant='primary' type='submit'>
              Search
            </Button>
          </Form>
        </Container>
      </Navbar>
      <div>
        <CarouselPokes pokemons={pokemons} getSpriteURL={getSpriteURL} />
      </div>
      <hr />
      <div className='d-flex flex-row justify-content-evenly p-3'>
        <div className='border border-dark p-3 col-12 col-md-4'>
          <TeamForm pokemons={pokemons} teams={teams} setTeams={setTeams} />
        </div>
        <div className='border border-dark p-3 col-12 col-md-4'>
          <TeamAccordion
            getSpriteURL={getSpriteURL}
            teams={teams}
            setTeams={setTeams}
            error={error}
            setError={setError}
            pokemons={pokemons}
          />
          <div className='text-center'>
            <Button
              className='m-2'
              variant='danger'
              id='deleteAll'
              onClick={() => {
                deleteAllTeams;
                const newTeams = [];
                setTeams(newTeams);
              }}>
              Delete All
            </Button>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
