import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TeamForm({ baseURI, pokemons, error, setError, getSpriteURL }) {
  const [members, setMembers] = useState([]);
  const [submitDisable, setSubmitDisable] = useState(true);
  const [selectDisable, setSelectDisable] = useState(false);

  const defaultState = {
    name: '',
    member: '',
  };

  const [nameInputState, setName] = useState(defaultState);
  const [memberInputState, setMember] = useState(defaultState);

  const addPokemon = () => {
    const pokemon = pokemons.filter(
      (p) => p.species === memberInputState.toUpperCase()
    );
    if (pokemon.length !== 0) {
      const newMembers = [...members, pokemon[0]];
      setMembers(newMembers);
    }
    console.log(members);
  };

  const nameInputChange = (input, value) => {
    const newNameInputState = { ...nameInputState, [input]: value };
    setName(newNameInputState);
    if (memberInputState.length > 0) {
      setSubmitDisable(false);
    }
    console.log(nameInputState);
  };

  const memberInputChange = (input, value) => {
    const newMemberInputState = { ...memberInputState, [input]: value };
    setMember(newMemberInputState);
    if (memberInputState.length > 0) {
      setSubmitDisable(false);
    }
    console.log(nameInputState);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
  };

  return (
    <>
      <Form onSubmit={handleSubmit}>
        <Form.Group className='mb-3' controlId='name'>
          <Form.Control
            type='text'
            value={nameInputState.name}
            placeholder='Enter the team name'
            onChange={(event) => {
              console.log(event.target.value);
              nameInputChange(event.target.id, event.target.value);
            }}
          />
        </Form.Group>

        <div className='text-center'>
          <Form.Group className='mb-3' controlId='member'>
            <Form.Control
              type='text'
              value={memberInputState.member}
              placeholder='Enter the pokemon'
              onChange={(event) => {
                console.log(event.target.value);
                memberInputChange(event.target.id, event.target.value);
              }}
            />
          </Form.Group>
          <Button variant='primary' id='addToTeam' onClick={addPokemon}>
            <i className='fa fa-plus'></i>
          </Button>
        </div>

        {/*
        <Form.Select
          disabled={selectDisable}
          className='mb-3'
          aria-label='Select species'
          onChange={(event) => {
            setSubmitDisable(false);
            const species = event.currentTarget.value;
            if (species.length > 0) {
              pokemons.forEach((pokemon) => {
                if (pokemon.species === species) {
                  console.log(pokemon);
                  const newMembers = [...members, pokemon];
                  setMembers(newMembers);
                  console.log(members);
                }
              });
            }
          }}>
          <option></option>
          {pokemons.map((pokemon) => (
            <option key={pokemon.dexNumber} value={pokemon.species}>
              {pokemon.species.toLowerCase()}
            </option>
          ))}
        </Form.Select>
        */}

        <div className='m-3'>
          {members.map((member) => (
            <span key={member.dexNumber} className='mx-2'>
              <img
                src={getSpriteURL(member.species)}
                className='border border-dark rounded'
                alt='pokeSprite'
              />
            </span>
          ))}
        </div>

        <div className='text-center'>
          <Button
            variant='primary'
            type='submit'
            id='btnSubmit'
            disabled={submitDisable}>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
