import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { InputGroup } from 'react-bootstrap';

function TeamForm({ baseURI, pokemons, error, setError }) {
  const [inputErrors, setInputErrors] = useState({});

  const defaultState = {
    name: '',
    members: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  /*
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(baseURI, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(inputState),
      });
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }

      const data = await response.json();
      const newSubscribers = [...subscribers, data];
      setSubscribers(newSubscribers);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };
  */

  return (
    <>
      <Form>
        <InputGroup className='mb-3'>
          <InputGroup.Text id='name'>Name</InputGroup.Text>
          <Form.Control
            type='text'
            value={inputState.name}
            placeholder='Enter the team name'
            onChange={(e) => {
              handleInputChange(e.target.id, e.target.value);
            }}
            isInvalid={inputErrors.name ? true : false}
          />
          <Form.Control.Feedback type='invalid'>
            {inputErrors.name}
          </Form.Control.Feedback>
        </InputGroup>

        <Form.Select aria-label='Select pokemon species'>
          {pokemons.map((pokemon) => (
            <option value={pokemon.dexNumber}>
              {pokemon.species.toLowerCase()}
            </option>
          ))}
        </Form.Select>
        <span className='border border-dark p-5'></span>
        <div className='text-center'>
          <Button variant='primary' type='submit'>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
