import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TeamForm({ baseURI, pokemons, error, setError, getSpriteURL }) {
  const [members, setMembers] = useState([]);
  const [submitDisable, setSubmitDisable] = useState(true);

  const defaultState = {
    name: '',
    member: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const addPokemon = () => {
    const pokemon = pokemons.filter(
      (p) => p.species === inputState.member.toUpperCase()
    );
    if (pokemon.length > 0) {
      const newMembers = [...members, pokemon[0]];
      setMembers(newMembers);
    }
    console.log(members);
  };

  const findBySpecies = async (species) => {
    try {
      const response = await fetch(`baseURI/${species.toUpperCase()}`);
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const data = await response.json();
      const newMembers = [...members, pokemon[0]];
      setMembers(newMembers);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
  };

  return (
    <>
      <Form onSubmit={handleSubmit}>
        <Form.Group className='mb-3' controlId='name'>
          <Form.Control
            type='text'
            value={inputState.name}
            placeholder='Enter the team name'
            onChange={(event) => {
              console.log(event.target.value);
              handleInputChange(event.target.id, event.target.value);
            }}
          />
        </Form.Group>

        <div className='text-center'>
          <Form.Group className='mb-3' controlId='member'>
            <Form.Control
              type='text'
              value={inputState.member}
              placeholder='Enter the pokemon'
              onChange={(event) => {
                console.log(event.target.value);
                handleInputChange(event.target.id, event.target.value);
              }}
            />
          </Form.Group>
          <Button variant='primary' id='addToTeam' onClick={addPokemon}>
            <i className='fa fa-plus'></i>
          </Button>
        </div>

        <div className='m-3'>
          {members.map((member) => (
            <span key={member.dexNumber} className='mx-2'>
              <img
                src={getSpriteURL(member.species)}
                className='border border-dark rounded'
                alt='pokeSprite'
              />
            </span>
          ))}
        </div>

        <div className='text-center'>
          <Button
            variant='primary'
            type='submit'
            id='btnSubmit'
            disabled={submitDisable}>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
