import { useState } from 'react';

const TeamAPI = () => {
  const [teams, setTeams] = useState([]);
  const [error, setError] = useState('');

  const createTeam = async (team) => {
    try {
      const response = await fetch('http://localhost:8080/api/v1/teams', {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
        },
        body: JSON.stringify(team),
      });
      console.log(response);
    } catch (e) {
      alert(e.message);
    }
  };

  const findAllTeams = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/v1/teams');
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const teams = await response.json();
      setTeams(teams);
    } catch (e) {
      alert(e.message);
    }
  };

  const updateTeam = async (team) => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/v1/teams/${team.id}`,
        {
          method: 'PUT',
          headers: {
            'Content-type': 'application/json',
          },
          body: JSON.stringify(team),
        }
      );
      console.log(response);
    } catch (e) {
      alert(e.message);
    }
  };

  const deleteTeamById = async (teamId) => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/v1/teams/${teamId}`,
        {
          method: 'DELETE',
        }
      );
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const newTeams = teams.filter((t) => t.id !== teamId);
      setTeams(newTeams);
    } catch (e) {
      alert(e.message);
    }
  };

  const deleteAllTeams = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/teams`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const newTeams = [];
      setTeams(newTeams);
    } catch (error) {
      alert(e.message);
    }
  };
};

export default TeamAPI;
