import Card from 'react-bootstrap/Card';
import PokePopover from './PokePopover';

const PokemonCard = ({ pokemon }) => {
  const openInNewTab = (URL) => {
    window.open(URL, '_blank', 'noreferrer');
  };

  const getStringDexNumber = (dexNumber) => {
    let stringDexNumber = dexNumber;
    if (dexNumber < 10) {
      stringDexNumber = `00${dexNumber}`;
    } else if (dexNumber < 100) {
      stringDexNumber = `0${dexNumber}`;
    }
    return stringDexNumber;
  };

  const getSpriteURL = (species) => {
    switch (species) {
      case 'NIDORAN_F':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-f.png';
      case 'NIDORAN_M':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/nidoran-m.png';
      case 'MR_MIME':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/mr-mime.png';
      case 'FARFETCH_D':
        return 'https://img.pokemondb.net/sprites/sword-shield/icon/farfetchd.png';
    }
    return `https://img.pokemondb.net/sprites/sword-shield/icon/${species.toLowerCase()}.png`;
  };

  return (
    <>
      <Card className='text-center'>
        <Card.Header>
          <img src={getSpriteURL(pokemon.species)} alt={pokemon.spAttack} />
        </Card.Header>
        <Card.Title className='mt-2'>
          {pokemon.species} #{getStringDexNumber(pokemon.dexNumber)}
        </Card.Title>
        <Card.Body className='pt-0'>
          <Button
            className='d-flex justify-content-between'
            variant='primary'
            id='splashArt'
            onClick={() => {
              window.open(
                'http://pearlsaurus.fc2web.com/elog/386/001.jpg',
                '_blank'
              );
            }}>
            <i className='fa fa-trash'></i>
          </Button>
          <Card.Text>
            <div className='pt-2'>
              <p>Attack: {pokemon.attack}</p>
              <p>Defense: {pokemon.defense}</p>
              <p>SpAttack: {pokemon.spAttack}</p>
              <p>SpDefense: {pokemon.spDefense}</p>
              <p>Speed: {pokemon.speed}</p>
            </div>
          </Card.Text>
        </Card.Body>
      </Card>
    </>
  );
};

export default PokemonCard;
