import Button from 'react-bootstrap/Button';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

const PokePopover = ({ pokemon }) => {
  return (
    <>
      {['bottom'].map((bottom) => (
        <OverlayTrigger
          trigger='click'
          key={bottom}
          placement={bottom}
          overlay={
            <Popover id={`popover-positioned-${bottom}`}>
              <Popover.Body>
                <img
                  src='http://pearlsaurus.fc2web.com/elog/386/009.jpg'
                  alt={pokemon.species}
                  width='100%'
                />
              </Popover.Body>
            </Popover>
          }>
          <Button variant='secondary'>SPLASH ART</Button>
        </OverlayTrigger>
      ))}
    </>
  );
};

export default PokePopover;
