import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const PokeModal = ({ dexNumber, getStringDexNumber }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getImageURL = (stringDexNumber) => {
    switch (stringDexNumber) {
      case '003' ||
        '006' ||
        '009' ||
        '0012' ||
        '0016' ||
        '0024' ||
        '0031' ||
        '0037' ||
        '0048' ||
        '0056' ||
        '0058' ||
        '0062' ||
        '0065' ||
        '0075' ||
        '0077':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_2.jpg`;
      case '018' ||
        '026' ||
        '038' ||
        '042' ||
        '068' ||
        '071' ||
        '076' ||
        '078' ||
        '082':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_700.jpg`;
      case '043' || '53':
        return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}_3.jpg`;

      case '059':
        return 'http://pearlsaurus.fc2web.com/elog/386/059_2_700.jpg';

      case '068':
        return 'http://pearlsaurus.fc2web.com/elog/386/068_700.jpg';
      case '071':
        return 'http://pearlsaurus.fc2web.com/elog/386/071_700.jpg';
      case '076':
        return 'http://pearlsaurus.fc2web.com/elog/386/076_700.jpg';
    }
    return `http://pearlsaurus.fc2web.com/elog/386/${stringDexNumber}.jpg`;
  };

  return (
    <>
      <Button variant='primary' onClick={handleShow}>
        SPLASH ART
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton />
        <Modal.Body>
          <img src={getImageURL(getStringDexNumber(dexNumber))} width='100%' />
        </Modal.Body>
      </Modal>
    </>
  );
};

export default PokeModal;
