import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import CarouselPokes from './components/CarouselPokes';
import { useState, useEffect } from 'react';
import { searchPokemons } from './APIs/PokemonAPI';

function App() {
  const [pokemons, setPokemons] = useState([]);
  let [error, setError] = useState('');

  const populatePokedex = async (species) => {
    const responseData = await searchPokemons(species);
    setPokemons(responseData);
  };

  useEffect(() => {
    populatePokedex();
  }, []);

  const handleSubmit = (value) => {};

  return (
    <>
      <Navbar expand='lg' variant='dark' bg='dark'>
        <Container id='nav-container'>
          <Navbar.Brand>POKEDEX</Navbar.Brand>
          <Form
            className='d-flex'
            onSubmit={(event) => {
              event.preventDefault();
              handleSubmit(event.target.value);
            }}>
            <Form.Control
              type='search'
              placeholder='Search'
              className='me-2'
              aria-label='Search'
            />
            <Button variant='primary' type='submit'>
              Search
            </Button>
          </Form>
        </Container>
      </Navbar>
      <div>
        <div>
          <CarouselPokes
            pokemons={pokemons}
            setPokemons={setPokemons}
            error={error}
            setError={setError}
          />
        </div>
        <hr />
        {/*
        <div className='d-flex justify-content-center pt-3'>
          <span className='border border-dark rounded p-5'>
            <SubscriberForm
              baseURI={baseURI}
              subscribers={subscribers}
              setSubscribers={setSubscribers}
              error={error}
              setError={setError}
            />
          </span>
        </div>
        */}
      </div>
    </>
  );
}

export default App;
