import { useState, useEffect } from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Button from 'react-bootstrap/Button';

function TeamAccordion({ getSpriteURL, error, setError }) {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    getAllTeams();
  }, []);

  const getAllTeams = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/v1/teams');
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const teams = await response.json();
      setTeams(teams);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const deleteTeam = async (teamId) => {
    const newTeams = teams.filter((t) => t.id !== teamId);
    setTeams(newTeams);
    try {
      const response = await fetch(
        `http://localhost:8080/api/v1/teams/${teamId}`,
        {
          method: 'DELETE',
        }
      );
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  return (
    <>
      <Accordion>
        {teams.map((team) => (
          <Accordion.Item eventKey={team.id}>
            <Accordion.Header>
              <Button
                className='d-flex justify-content-between m-2'
                variant='danger'
                id='delete'
                onClick={() => {
                  deleteTeam(team.id);
                }}>
                <i className='fa fa-trash'></i>
              </Button>
              Team '{team.name}'
            </Accordion.Header>
            <Accordion.Body>
              {team.members.map((member) => (
                <img src={getSpriteURL(member.species)} alt={member.species} />
              ))}
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
    </>
  );
}

export default TeamAccordion;
