import Accordion from 'react-bootstrap/Accordion';
import Button from 'react-bootstrap/Button';
import UpdatePopover from './UpdatePopover';

const TeamAccordion = ({ getSpriteURL, error, setError, teams, setTeams }) => {
  const deleteTeam = async (teamId) => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/v1/teams/${teamId}`,
        {
          method: 'DELETE',
        }
      );
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }
      const newTeams = teams.filter((t) => t.id !== teamId);
      setTeams(newTeams);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const popover = (
    <Popover id='popover-basic'>
      <Popover.Header as='h3'>Popover right</Popover.Header>
      <Popover.Body>
        And here's some <strong>amazing</strong> content. It's very engaging.
        right?
      </Popover.Body>
    </Popover>
  );

  return (
    <>
      <Accordion>
        {teams.map((team) => (
          <Accordion.Item eventKey={team.id}>
            <Accordion.Header>
              <OverlayTrigger trigger='click' placement='top' overlay={popover}>
                <Button
                  className='d-flex justify-content-between m-2'
                  variant='primary'
                  id='delete'>
                  <i className='fa fa-gear' />
                </Button>
              </OverlayTrigger>
              <Button
                className='d-flex justify-content-between m-2'
                variant='danger'
                id='delete'
                onClick={() => {
                  deleteTeam(team.id);
                }}>
                <i className='fa fa-trash' />
              </Button>
              Team '{team.name}'
            </Accordion.Header>
            <Accordion.Body>
              {team.members.map((member) => (
                <img src={getSpriteURL(member.species)} alt={member.species} />
              ))}
            </Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
    </>
  );
};

export default TeamAccordion;
