import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TeamForm({ baseURI, subscribers, setSubscribers, error, setError }) {
  const defaultState = { members: '' };

  const [inputState, setInputState] = useState(defaultState);
  const [inputErrors, setInputErrors] = useState({});

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(baseURI, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(inputState),
      });
      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }

      const data = await response.json();
      const newSubscribers = [...subscribers, data];
      setSubscribers(newSubscribers);
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  return (
    <>
      <Form onSubmit={handleSubmit}>
        <Form.Group className='mb-3'>
          <Form.Control
            placeholder='Username'
            aria-label='Username'
            aria-describedby='basic-addon1'
          />
          <Form.Select aria-label='Default select example'>
            <option>Open this select menu</option>
            <option value='1'>One</option>
            <option value='2'>Two</option>
            <option value='3'>Three</option>
          </Form.Select>
        </Form.Group>
        <div className='text-center'>
          <Button variant='primary' type='submit'>
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default TeamForm;
