export const findAllPokemon = async () => {
  try {
    const response = await fetch('http://localhost:8080/api/v1/pokemons');
    if (!response.ok) {
      throw new Error(`Error! status: ${response.status}`);
    }
    const pokemons = await response.json();
    return pokemons;
  } catch (e) {
    error = e.message;
    setError(error);
  }
};
