import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

const UpdateModal = ({
  id,
  team,
  teams,
  setTeams,
  error,
  setError,
  pokemons,
}) => {
  const [show, setShow] = useState(false);

  const handleClose = (updatedTeam) => {
    const newTeams = () => {
      teams.forEach((team) => {
        if (team.id === updatedTeam.id) {
          teams[teams.indexOf(team)] = updatedTeam;
        }
      });
    };
    setTeams(newTeams);
    setShow(false);
  };

  const handleShow = () => setShow(true);

  const defaultState = {
    name: '',
    speciesToRemove: '',
    speciesToAdd: '',
  };

  const [inputState, setInputState] = useState(defaultState);

  const handleInputChange = (input, value) => {
    const newInputState = { ...inputState, [input]: value };
    setInputState(newInputState);
  };

  const updateTeam = async (id) => {
    const team = protoTeamBuilder();
    try {
      const updatedTeam = await fetch(
        `http://localhost:8080/api/v1/teams/${id}`,
        {
          method: 'PUT',
          headers: {
            'Content-type': 'application/json',
          },
          body: JSON.stringify(team),
        }
      );
      return updatedTeam;
    } catch (e) {
      error = e.message;
      setError(error);
    }
  };

  const protoTeamBuilder = () => {
    const newTeamMembers = [];
    const speciesToRemoveArray = inputState.speciesToRemove.split(',');
    const SpeciesToAddArray = inputState.speciesToAdd.split(',');

    team.members.forEach((member) => {
      speciesToRemoveArray.forEach((species) => {
        if (member.species !== species.toUpperCase()) {
          newTeamMembers.push(member);
        }
      });
    });

    SpeciesToAddArray.forEach((species) => {
      pokemons.forEach((pokemon) => {
        if (pokemon.species === species.toUpperCase()) {
          newTeamMembers.push(pokemon);
        }
      });
    });

    const newTeam = {
      name: inputState.name !== '' ? inputState.name : team.name,
      members: newTeamMembers,
    };
    console.log(newTeam);
    return newTeam;
  };

  return (
    <>
      <Button
        onClick={handleShow}
        className='d-flex justify-content-between m-2'
        variant='primary'
        id='delete'>
        <i className='fa fa-gear' />
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <Form>
            <Form.Group className='mb-3' controlId='name'>
              <Form.Control
                type='text'
                value={inputState.name}
                placeholder='Enter the new team name'
                onChange={(event) => {
                  console.log(event.target.value);
                  handleInputChange(event.target.id, event.target.value);
                }}
              />
            </Form.Group>
            <Form.Group className='mb-3' controlId='speciesToRemove'>
              <Form.Control
                type='text'
                value={inputState.speciesToRemove}
                placeholder='Enter pokemons to remove separated by comma'
                onChange={(event) => {
                  handleInputChange(event.target.id, event.target.value);
                }}
              />
            </Form.Group>
            <Form.Group className='mb-3' controlId='speciesToAdd'>
              <Form.Control
                type='text'
                value={inputState.speciesToAdd}
                placeholder='Enter pokemons to add separated by comma'
                onChange={(event) => {
                  handleInputChange(event.target.id, event.target.value);
                }}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={handleClose}>
            Close
          </Button>
          <Button
            variant='primary'
            onClick={() => {
              const updatedTeam = updateTeam(id);
              handleClose(updatedTeam);
            }}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default UpdateModal;
